import com.rameses.common.annotations.*;
import com.rameses.rcp.common.*;


public class AfterUpdateTestController {

    def name;
    def address;
    def jobTitle;

    void search() {
        println "firing test....";
        if ( name == "JAYROME" ) {
            address = "LAHUG, CC";
            jobTitle = "SOFTWARE DEVLOPER";
        }
        else if ( name == "ALVIN" ) {
            address = "TALAMBAN, CC";
            jobTitle = "SOFTWARE DEVLOPER";
        }
        else {
            address = "<NO RECORD FOUND>";
            jobTitle = "<NO RECORD FOUND>";
        }
    }

}
