import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;


public class ControlsTestController  {

    Date date = new Date();
    int intNum;
    double doubleNum;
    BigDecimal decimalNum = 10.1;

    def checkBox1;
    def checkBox2;
    def memo;

    def item = [name:"Minglanilla"];
    def item2;
    def item3;
    def itemList = [[name:"Cebu City"], [name:"Minglanilla"], [name:"Naga"]];

    def file;

    void btnShowInput() {
        MsgBox.alert("""
            date = $date
            intNum = $intNum
            doubleNum = $doubleNum
            decimalNum = $decimalNum
            item = $item
            item2 = $item2
            item3 = $item3
            checkBox1 = $checkBox1
            checkBox2 = $checkBox2
            memo = $memo
        """);
    }

    boolean readonly;

    void btnToggle() {
        readonly = !readonly;
    }

    def actions = [
        new Action(name: '_close', caption: 'Close', immediate: true),
        new Action(name: 'edit', caption: 'Edit'),
        new Action(name: 'print', caption: 'Print'),
        new Action(name: 'btnToggle', caption: 'Toggle', immediate: true)
    ];

}
