import com.rameses.common.annotations.*;
import com.rameses.rcp.common.*;
import test.model.EmployeeType;


public class NavigationTestController  {

    String firstname;
    String lastname;
    String address;
    boolean single;
    EmployeeType employeeType;

    public String next() {
        return "page2";
    }

    public Object launch() {
        return new WindowOpener(name: "rameses-client-ui2-test-impl:navTest2");
    }

    public Object launch2() {
        return new Opener(name: "rameses-client-ui2-test-impl:navTest2");
    }


    public String back() {
        return "default";
    }

    public void greet() {
        MsgBox.alert("Hi " + getFullname());
        System.out.println("single is " + single);
        System.out.println("employee type is " + employeeType);
    }

    public String getFullname() {
        return (firstname == null? "" : firstname + " ") + (lastname == null? "": lastname);
    }
}
