import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;


public class Test1Controller  {

    @Binding
    def binding;

    def doNext() {
        return new Opener(name: 'test-client:test2');
    }

    void select( data ) {
        if ( data == null ) {
            binding.fireNavigation(
                new Opener(name: 'test-client:_crudFormTest')
            );
        }
    }

}
