import com.rameses.rcp.annotations.*;
import com.rameses.rcp.common.*;


public class Test1Controller  {

    @Caller
    def caller;

    @Binding
    def binding;        

    void select() {
        binding.fireNavigation('_close');
        caller.select( [:] );
    }

    void cancel() {
        binding.fireNavigation('_close');
        caller.select( null );
    }

}
